import "./App.css"
import React, { createContext, useState } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Layout from "./pages/Layout";
import TokenContext, { TokenProvider } from "./pages/TokenContext"
import Home from "./pages/Home";
import CenPlus from "./pages/CenPlus";
import Contact from "./pages/Contact";
import NoPage from "./pages/NoPage";
import Signinup from "./pages/Signinup";
import SignUpDetails from "./pages/SignUpDetails";
import Events from "./pages/Events";
import ForgotPassword from "./pages/ForgotPassword";

export default function App() {
  const { token, updateToken, user, updateUser, loggedIn, setLoggedIn } = useContext(TokenContext);
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setLoggedIn(true);
      setToken(storedToken);

      // Also, you may want to fetch user data using the token
    }
  }, []);
  return (
    <TokenProvider>
    <BrowserRouter>
      <Routes>

          <Route path="/" element={<Layout />}>
          <Route  index element={<Home />} />
          <Route path="Events" element={<Events />} />
          <Route path="CenPlus" element={<CenPlus />} />
          <Route path="contact" element={<Contact />} />
          <Route path="Signinup" element={<Signinup/>} />
          <Route path="SignUpDetails" element={<SignUpDetails/>} />
          <Route path="ForgotPassword" element={<ForgotPassword/>} />
          <Route path="*" element={<NoPage />} />
          

        </Route>


      </Routes>
    </BrowserRouter>

    </TokenProvider>
  );
}
