import React, { createContext, useState , useEffect } from 'react';

const TokenContext = createContext();

export const TokenProvider = ({ children }) => {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);
  const [loggedIn, setLoggedIn] = useState(null);
  const updateToken = (newToken) => {
    setToken(newToken);
  };

  const updateUser = (newUser) => {
    setUser(newUser);
  };

  

useEffect(() => {
  const storedToken = localStorage.getItem('token');
  const storedUser = localStorage.getItem('user');
  const storedUsername = localStorage.getItem('username');

  if (storedToken !== undefined && storedToken !== null) {
    setLoggedIn(true);
    setToken(storedToken);
    setUser(storedUser ? JSON.parse(storedUser) : null); // Parse the user data if it exists or set to null
    // Set the username from local storage to the state
    if (storedUsername) {
      // Make sure to parse the storedUsername as JSON if it's stored as a JSON string
      setUser((prevUser) => ({ ...prevUser, username: JSON.parse(storedUsername) }));
    }
    // Also, you may want to fetch user data using the token
  }
}, []);

  const  [storedToken, strdToken] = useState(localStorage.getItem('token'));

  return (
    <TokenContext.Provider value={{ token, updateToken, user, updateUser, loggedIn, setLoggedIn }}>
      {children}
    </TokenContext.Provider>
  );
};

export default TokenContext;
