import jwtDecode from 'jwt-decode';

const TokenContext = createContext();

export const TokenProvider = ({ children }) => {
  const [token, setToken] = useState('');
  const [user, setUser] = useState('');
  const [loggedIn, setLoggedIn] = useState('');

  const updateToken = (newToken) => {
    setToken(newToken);
  };

  const updateUser = (newUser) => {
    setUser(newUser);
  };

  

  // page refresher 
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    console.log(storedUser);
    console.log(storedToken !== undefined && storedToken !== null);
    if (storedToken !== '' && storedToken !== null) {
        console.log('ksm so bad ');
       setLoggedIn(true);
       setToken(storedToken);
        const decodedToken = jwtDecode(token);
        setUser(decodedToken);
       //Also, you may want to fetch user data using the token
   }
   
  }, []);

  const  [storedToken, strdToken] = useState(localStorage.getItem('token'));

  return (
    <TokenContext.Provider value={{ token, updateToken, user, updateUser, loggedIn, setLoggedIn }}>
      {children}
    </TokenContext.Provider>
  );
};

export default TokenContext;
