import React, { useContext, useEffect, useState } from 'react';
import './ProfilePage.css';
import TokenContext from './TokenContext';

const ProfilePage = () => {
const { token, updateToken, user, updateUser, loggedIn, setLoggedIn } = useContext(TokenContext);
// to be changed so that we dont give away the ability to be connected to someones page in the code 
console.log(user);
console.log('the profile');
const handleSignUpClick = () => {
  // Redirect the user to the desired email address
  window.location.href = '/EventCreator';
};
  return (
    <div className='signuppage'>
      <div className='signupform'>
        <h1>Join Us Today!</h1>
        <p>Sign up and start exploring our platform!</p>
        <form  className="formReg">
          <div className="inputField">
            <label htmlFor="firstName">First Name*</label>
            <input type="text" id="firstName" placeholder="Enter your first name" 
            value={name}
            onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="inputField">
            <label htmlFor="lastName">Last Name*</label>
            <input type="text" id="lastName" placeholder="Enter your last name"
            value={surname}
            onChange={(e) => setSurname(e.target.value)}
            />
            
          </div>


          <div className="inputField">
            <label htmlFor="email">Email*</label>
            <input type="email" id="email" placeholder="Enter your email"
            value={mail}
            onChange={(e) => setEmail(e.target.value)}
            />

          
          
          </div>

          <div className="inputField">
            <label htmlFor="user">Username*</label>
            <input type="user" id="email" placeholder="Enter your username"
            value={real_username}
            onChange={(e) => setusername(e.target.value)}
            /> 
          </div>

          <div className="inputField">
            <label htmlFor="password">Password*</label>
            <input type="password" id="password" placeholder="Enter your password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <span className="password-length-info">(Password must be longer than 8 characters)</span>
          <button type="submit" className="btnformReg" onClick={handleSubmit}>
            SIGN UP
          </button>
        </form>
        <span>Already have an account? <a href="./Signinup">Sign In</a></span>
      </div>
    </div>
  );
};

export default ProfilePage;
