import { Outlet, Link } from "react-router-dom";
import "./Layout.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown } from '@fortawesome/free-solid-svg-icons'
import Logo from "../images/Untitled design (1).png"
import React, { useContext  , useState } from 'react';
import TokenContext from './TokenContext';
import axios from "axios";

const Layout = () => {
  const { token, updateToken, user, updateUser, loggedIn, setLoggedIn } = useContext(TokenContext);


  const handleLogOutClick = async () => {
    try {
      //await axios.post("http://localhost:3001/logout");
      updateToken('');
        updateUser('');
        setLoggedIn(false)// Update the state to reflect the user has logged out
        localStorage.setItem('token', '');
      localStorage.setItem('user', '');
      window.location.href = '/Signinup';
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  return (
    <div className="layout">
      <nav className="container navbar">
        <div className="logo">
        <img  className="logo-img" src= {Logo} size={80} />
        {/* <p className='logo-text'>
    CentrlEye
   </p> */}
</div>
<menu>
        <ul className="nav-links">
          <li>
            <Link to="/">Home </Link><FontAwesomeIcon className='nav-down' icon={faAngleDown}/>
          
          </li>
          <li>
            <Link to = "/Events">Events</Link>
            <FontAwesomeIcon className='nav-down' icon={faAngleDown}/>
          </li>
          <li>
            <Link to="/contact">Contact</Link> <FontAwesomeIcon className='nav-down' icon={faAngleDown}/>
         
          </li>
          <li>
          <Link to="/CenPlus">CentrlEye+</Link>
          
          </li>
          {!loggedIn ? (
              <li id="login">
                <Link to="/Signinup">Login</Link>
              </li>
            ) : (
              <button type='submit'    onClick={handleLogOutClick} className='btnform'>
              Log out
            </button>
            )}
          
        </ul>
        </menu>
      </nav>

      <Outlet />
    </div>
  )
};

export default Layout;
