import React, { useState, useEffect } from 'react';
import './Signinup.css';
import axios from "axios";
const Signupin = () => {

  const [username, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState("");

  const handleSignInClick = async (event) => {
    event.preventDefault();

    // Send a POST request to your server with email and password
    try {
      const response=  await  axios.post("http://localhost:3001/login", {
         username: username,
         password: password,
       })
 
       if (response.status == 200) {
         alert('User signed up successfully!');
         // Handle success, e.g., redirect to a new page or update UI
       } else {
         alert('Failed to sign up');
       }
     } catch (error) {
       console.error('Error signing up:', error);
       alert('Failed to sign up');
     }
    
  };
  const handleSignUpClick = () => {
    // Redirect the user to the desired email address
    window.location.href = '/SignUpDetails';
  };

  const handlePassword = () => {
    // Redirect the user to the desired email address
    window.location.href = '/ForgotPassword';
  };

  return ( 
    <div className='userlogin'>
      <div className='signin'>
        <h1>Sign in</h1>
        <span>or use your account</span>
        <form className='form'>
          <fieldset>
            <input type='email'
             onChange={(e) => setEmail(e.target.value)}
            placeholder='Email' />
          </fieldset>
          <fieldset>
            <input type='password'
            onChange={(e) => setPassword(e.target.value)}
            placeholder='Password' />
          </fieldset>
          <button type='submit'    onClick={handleSignInClick} className='btnform'>
       
            SIGN IN
          </button>
        </form>
        <span className='forgotpassword' onClick={handlePassword}>
          Forgot your password?
        </span>
      </div>
  
      <div className='signup'>
        <div className='signup1'>
          <h1>Join Us Today!</h1>
        </div>
        <div className='signup2'>
          Look for events and grow your business!
        </div>
        <button type='submit' className='btnsignup' onClick={handleSignUpClick}>
          SIGN UP
        </button>
      </div>
    </div>
  );
};

export default Signupin;
