import React, { useContext, useState } from 'react';
import './Signinup.css';
import axios from 'axios';
import TokenContext from './TokenContext';

const Signupin = () => {
  const [password, setPassword] = useState('');

  const { token, updateToken, user, updateUser, loggedIn, setLoggedIn } = useContext(TokenContext);

  const handleSignInClick = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/login', {
        username: user.username,
        password: password,
      });

      if (response.data.message === '555') {
        alert('Failed to sign in');
      } else {
        setLoggedIn(true);
        const { user, token } = response.data;
        updateToken(token);
        updateUser(user);
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
      }
    } catch (error) {
      console.error('Error signing in:', error);
      alert('Failed to sign in');
    }
  };

  const handleSignUpClick = () => {
    window.location.href = '/SignUpDetails';
  };

  const handlePassword = () => {
    window.location.href = '/ForgotPassword';
  };

  const handleLogOutClick = () => {
    updateToken('');
    updateUser(null);
    setLoggedIn(false);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  };

  return (
    <div>
      {user ? (
        <div className='welcome-message'>
          <h1>Hello {user.username}!</h1>
          <button onClick={handleLogOutClick}>Log Out</button>
        </div>
      ) : (
        <div className='userlogin'>
          <div className='signin'>
            <h1>Sign in</h1>
            <span>or use your account</span>
            <form className='form'>
              <fieldset>
                <input
                  type='email'
                  value={user?.username || ''}
                  placeholder='Email'
                  readOnly
                />
              </fieldset>
              <fieldset>
                <input
                  type='password'
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder='Password'
                />
              </fieldset>
              <button type='submit' onClick={handleSignInClick} className='btnform'>
                SIGN IN
              </button>
            </form>
            <span className='forgotpassword' onClick={handlePassword}>
              Forgot your password?
            </span>
          </div>

          <div className='signup'>
            <div className='signup1'>
              <h1>Join Us Today!</h1>
            </div>
            <div className='signup2'>
              Look for events and grow your business!
            </div>
            <button type='submit' className='btnsignup' onClick={handleSignUpClick}>
              SIGN UP
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Signupin;
