import React, { useState } from 'react';
import "./Signinup.css";

const Signupin = () => {
  // State variables for email and password
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // Function to handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    // Send a POST request to your Node.js server
    try {
      const response = await fetch('/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        alert('User signed up successfully!');
        // Handle success, e.g., redirect to a new page
      } else {
        const data = await response.json();
        alert(data.error || 'Failed to sign up');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('Failed to sign up');
    }
  };

  const handleSignUpClick = () => {
    // Redirect the user to the desired email address
    window.location.href = '/SignUpDetails';
  };

  return (
    <div className='userlogin'>
      <div className='signin'>
        <h1>Sign in</h1>
        <span>or use your account</span>
        <form onSubmit={handleSubmit} className="form">
          <fieldset className="email">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </fieldset>
          <fieldset className="password">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </fieldset>

          <button type="submit" className="btnform">SIGN IN</button>
        </form>
        <span>Forgot your password?</span>
      </div>

      <div className='signup'>
        <div className='signup1'>
          <h1>Join Us Today!</h1>
        </div>
        <div className='signup2'>
          Look for events and grow your business!
        </div>
        <button type="submit" className="btnsignup" onClick={handleSignUpClick}>SIGN UP</button>
      </div>
    </div>
  );
};

export default Signupin;
