import React, { createContext, useState , useEffect } from 'react';
import jwtDecode from 'jwt-decode';

const TokenContext = createContext();

export const TokenProvider = ({ children }) => {
  const [token, setToken] = useState('');
  const [user, setUser] = useState('');
  const [loggedIn, setLoggedIn] = useState('');

  const updateToken = (newToken) => {
    setToken(newToken);
  };

  const updateUser = (newUser) => {
    setUser(newUser);
  };

  

  // page refresher 
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    console.log(storedToken)
    console.log("token stored")
    updateToken(storedToken); 
    if (storedToken !== '' ) {
       //setToken(storedToken);  
       console.log('ksm so bad 1');
       console.log(token);
       if (token !== '') {
        try {

          const decodedToken = jwtDecode(token);
          setUser(decodedToken);
          console.log('ksm so bad 2');
          console.log(user);
          setLoggedIn(true);
        } catch (error) {
          // Handle decoding errors here
          console.error('Error decoding token:', error);
          setUser(null);
        }
      } else {
        setUser(null);
      }
       //Also, you may want to fetch user data using the token
   }
   
  }, []);

  //const  [storedToken, strdToken] = useState(localStorage.getItem('token'));

  return (
    <TokenContext.Provider value={{ token, updateToken, user, updateUser, loggedIn, setLoggedIn }}>
      {children}
    </TokenContext.Provider>
  );
};

export default TokenContext;
