import React, { useState, useEffect } from 'react';
import axios from "axios";
import "./Signinup.css";

const Signupin = () => {
  const [username, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignUpClick = () => {
    // Redirect the user to the desired email address
    window.location.href = '/SignUpDetails';
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Send a POST request to your server with email and password
    try {
     const response=  await  axios.post("http://localhost:3001/register", {
        username: username,
        password: password,
      })

      if (response.ok) {
        alert('User signed up successfully!');
        // Handle success, e.g., redirect to a new page or update UI
      } else {
        const data = await response.json();
        alert(data.error || 'Failed to sign up');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('Failed to sign up');
    }
  };

  return (
    <div className='userlogin'>
      <div className='signin'>
        <h1>Sign in</h1>
        <span>or use your account</span>
        <form onSubmit={handleSubmit} className="form">
          <fieldset className="email">
            <input
              type="Email"
              placeholder="username"
              value={username}
              onChange={(e) => setEmail(e.target.value)}
            />
          </fieldset>
          <fieldset className="password">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </fieldset>

          <button type="submit" className="btnform">SIGN IN</button>
        </form>
        <span>Forgot your password?</span>
      </div>

      <div className='signup'>
        <div className='signup1'>
          <h1>Join Us Today!</h1>
        </div>
        <div className='signup2'>
          Look for events and grow your business!
        </div>
        <button type="submit" className="btnsignup" onClick={handleSignUpClick}>SIGN UP</button>
      </div>
    </div>
  );
};

export default Signupin;
