import React, { useContext, useEffect, useState } from 'react';
import './EventCreator.css';
import axios from "axios";


import TokenContext from './TokenContext';

const EventCreator = () => {
    const [formData, setFormData] = useState({
        username: '',
        Description: '',
        Name: '',
        Category: '',
        private: false,
      });
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
          ...prevData,
          [name]: value,
        }));
      };
    
      const handleSubmit = (e) => {
        e.preventDefault();
        // Assuming your backend server is running on 'http://localhost:5000'
        const backendURL = 'http://localhost:5000/Cevent';
        
        // Assuming you have implemented token-based authentication and have the token in a variable 'token'
        const token = 'your-auth-token';
    
        // Include the token in the request headers
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };
    
        axios.post(backendURL, formData, config)
          .then((response) => {
            console.log('Event created successfully:', response.data);
            // You can perform any other actions here upon successful event creation.
          })
          .catch((error) => {
            console.error('Error creating event:', error);
            // Handle any errors that occurred during event creation.
          });
      };
    
      return (
        <form onSubmit={handleSubmit} className="event-form">
          <label>
            Username (Host):
            <input type="text" name="username" value={formData.username} onChange={handleChange} />
          </label>
          {/* ... Other form fields */}
          <button type="submit" className="events-btn">Create Event</button>
        </form>
      );
};

export default EventCreator;
